import React, { useState, useEffect } from 'react';

export default function CategoryList() {
  const [categories, setCategories] = useState(() => {
    const saved = localStorage.getItem('categories');
    return saved ? JSON.parse(saved) : [];
  });
  const [tasks, setTasks] = useState(() => {
    const saved = localStorage.getItem('tasks');
    return saved ? JSON.parse(saved) : [];
  });

  const [newCategory, setNewCategory] = useState('');
  const [selectedCategory, setSelectedCategory] = useState(null);

  const handleAddCategory = (e) => {
    e.preventDefault();
    if (newCategory.trim() === '') return;

    const updatedCategories = [...categories, newCategory.trim()];
    setCategories(updatedCategories);
    localStorage.setItem('categories', JSON.stringify(updatedCategories));
    setNewCategory('');
  };

  const handleDeleteCategory = (category) => {
    // Hapus kategori
    const filteredCategories = categories.filter(cat => cat !== category);
    setCategories(filteredCategories);
    localStorage.setItem('categories', JSON.stringify(filteredCategories));

    // Jika kategori yang dipilih dihapus, reset selectedCategory
    if (selectedCategory === category) {
      setSelectedCategory(null);
    }
  };

  // Filter tugas berdasarkan kategori yang dipilih
  const filteredTasks = selectedCategory
    ? tasks.filter(task => task.category === selectedCategory)
    : [];

  return (
    <div className="container mt-4">
      <h2>Manajemen Kategori</h2>

      <form onSubmit={handleAddCategory} className="mb-3 d-flex gap-2">
        <input
          type="text"
          className="form-control"
          placeholder="Tambah kategori baru..."
          value={newCategory}
          onChange={e => setNewCategory(e.target.value)}
        />
        <button className="btn btn-primary" type="submit">Tambah</button>
      </form>

      {categories.length === 0 ? (
        <p>Tidak ada kategori.</p>
      ) : (
        <ul className="list-group mb-4">
          {categories.map(cat => (
            <li
              key={cat}
              className={`list-group-item d-flex justify-content-between align-items-center ${selectedCategory === cat ? 'active' : ''}`}
              style={{ cursor: 'pointer' }}
              onClick={() => setSelectedCategory(cat)}
            >
              {cat}
              <button
                className="btn btn-danger btn-sm"
                onClick={e => {
                  e.stopPropagation(); // supaya klik tombol tidak trigger klik list
                  handleDeleteCategory(cat);
                }}
              >
                Hapus
              </button>
            </li>
          ))}
        </ul>
      )}

      {selectedCategory && (
        <>
          <h4>Tugas dalam kategori: {selectedCategory}</h4>
          {filteredTasks.length === 0 ? (
            <p>Tidak ada tugas dalam kategori ini.</p>
          ) : (
            <ul className="list-group">
              {filteredTasks.map(task => (
                <li key={task.id} className="list-group-item">
                  {task.title}
                </li>
              ))}
            </ul>
          )}
        </>
      )}
    </div>
  );
}
