import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

export default function TaskList() {
  const [tasks, setTasks] = useState(() => {
    const savedTasks = localStorage.getItem('tasks');
    return savedTasks ? JSON.parse(savedTasks) : [];
  });

  const [categories, setCategories] = useState(() => {
    const savedCategories = localStorage.getItem('categories');
    return savedCategories ? JSON.parse(savedCategories) : [];
  });

  const [newTask, setNewTask] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');
  const [searchTerm, setSearchTerm] = useState('');

  // Tambah tugas baru
  const handleAddTask = (e) => {
    e.preventDefault();
    if (newTask.trim() === '') return;

    const task = {
      id: Date.now(),
      title: newTask.trim(),
      category: selectedCategory || '',
    };

    const updatedTasks = [...tasks, task];
    setTasks(updatedTasks);
    localStorage.setItem('tasks', JSON.stringify(updatedTasks));
    setNewTask('');
    setSelectedCategory('');
  };

  // Hapus tugas
  const handleDeleteTask = (id) => {
    const filteredTasks = tasks.filter(task => task.id !== id);
    setTasks(filteredTasks);
    localStorage.setItem('tasks', JSON.stringify(filteredTasks));
  };

  // Filter tugas berdasarkan search term
  const filteredTasks = tasks.filter(task =>
    task.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="container mt-4">
      <h2>Daftar Tugas</h2>

      {/* Input pencarian */}
      <input
        type="text"
        className="form-control mb-4"
        placeholder="Cari tugas..."
        value={searchTerm}
        onChange={e => setSearchTerm(e.target.value)}
      />

      {/* Form tambah tugas */}
      <form onSubmit={handleAddTask} className="d-flex gap-2 mb-4">
        <input
          type="text"
          className="form-control"
          placeholder="Tambah tugas baru..."
          value={newTask}
          onChange={e => setNewTask(e.target.value)}
        />
        <select
          className="form-select"
          value={selectedCategory}
          onChange={e => setSelectedCategory(e.target.value)}
        >
          <option value="">Pilih Kategori (opsional)</option>
          {categories.map((cat, idx) => (
            <option key={idx} value={cat}>{cat}</option>
          ))}
        </select>
        <button className="btn btn-primary" type="submit">Tambah</button>
      </form>

      {/* Daftar tugas */}
      <div className="mb-5">
        {filteredTasks.length === 0 ? (
          <p>Tidak ada tugas yang cocok.</p>
        ) : (
          filteredTasks.map(task => (
            <div key={task.id} className="task-card mb-3">
              <Link to={`/tasks/${task.id}`} style={{ textDecoration: 'none' }}>
                <h5>{task.title}</h5>
              </Link>
              <p className="task-category">{task.category || '-'}</p>
              <button
                className="btn btn-danger btn-sm"
                onClick={() => handleDeleteTask(task.id)}
              >
                Hapus
              </button>
            </div>
          ))
        )}
      </div>

      {/* Daftar kategori */}
      <div>
        <h3>Kategori Tugas</h3>
        {categories.length === 0 ? (
          <p>Tidak ada kategori.</p>
        ) : (
          categories.map((cat, idx) => {
            const tasksByCat = tasks.filter(task => task.category === cat);
            return (
              <div key={idx} className="category-card mb-4 p-3 border rounded">
                <h5>{cat}</h5>
                {tasksByCat.length === 0 ? (
                  <p>Tidak ada tugas di kategori ini.</p>
                ) : (
                  <ul>
                    {tasksByCat.map(task => (
                      <li key={task.id}>{task.title}</li>
                    ))}
                  </ul>
                )}
              </div>
            );
          })
        )}
      </div>
    </div>
  );
}
