import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import { AuthProvider, useAuth } from './context/AuthContext';
import Login from './components/Auth/Login';
import Register from './components/Auth/Register';
import TaskList from './components/Dashboard/TaskList';
import PrivateRoute from './components/Auth/PrivateRoute';

function Navbar() {
  const { user, logout } = useAuth();

  return (
    <nav className="navbar navbar-expand navbar-dark bg-dark px-3">
      <Link className="navbar-brand" to="/">TugasKu</Link>
      <div className="navbar-nav">
        <Link className="nav-link" to="/">Beranda</Link>
        <Link className="nav-link" to="/about">Tentang</Link>
        {user && <Link className="nav-link" to="/tasks">Tugas</Link>}
        {!user && <Link className="nav-link" to="/login">Login</Link>}
        {!user && <Link className="nav-link" to="/register">Register</Link>}
        {user && (
          <>
            <span className="nav-link">Halo, {user.username}</span>
            <button onClick={logout} className="btn btn-outline-light btn-sm ms-2">Logout</button>
          </>
        )}
      </div>
    </nav>
  );
}

function Home() {
  return (
    <div className="container mt-4">
      <h1 className="text-primary">Halo, TugasKu!</h1>
      <p>Selamat datang di aplikasi manajemen tugas.</p>
    </div>
  );
}

function About() {
  return (
    <div className="container mt-4">
      <h2>Tentang Aplikasi</h2>
      <p>Aplikasi ini membantu kamu mengelola tugas harian dengan mudah.</p>
    </div>
  );
}

export default function App() {
  return (
    <AuthProvider>
      <Router>
        <Navbar />

        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/about" element={<About />} />
          <Route
            path="/tasks"
            element={
              <PrivateRoute>
                <TaskList />
              </PrivateRoute>
            }
          />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
        </Routes>
      </Router>
    </AuthProvider>
  );
}
