import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

export default function TaskList() {
  const [tasks, setTasks] = useState(() => {
    const savedTasks = localStorage.getItem('tasks');
    return savedTasks ? JSON.parse(savedTasks) : [];
  });

  const [categories, setCategories] = useState(() => {
    const savedCategories = localStorage.getItem('categories');
    return savedCategories ? JSON.parse(savedCategories) : [];
  });

  const [newTask, setNewTask] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');

  const handleAddTask = (e) => {
    e.preventDefault();
    if (newTask.trim() === '') return;

    const task = {
      id: Date.now(),
      title: newTask.trim(),
      category: selectedCategory || null,
    };

    const updatedTasks = [...tasks, task];
    setTasks(updatedTasks);
    localStorage.setItem('tasks', JSON.stringify(updatedTasks));
    setNewTask('');
    setSelectedCategory('');
  };

  const handleDeleteTask = (id) => {
    const filteredTasks = tasks.filter(task => task.id !== id);
    setTasks(filteredTasks);
    localStorage.setItem('tasks', JSON.stringify(filteredTasks));
  };

  return (
    <div className="container mt-4">
      <h2>Daftar Tugas</h2>
      <form onSubmit={handleAddTask} className="mb-3 d-flex gap-2">
        <input
          type="text"
          className="form-control"
          placeholder="Tambah tugas baru..."
          value={newTask}
          onChange={e => setNewTask(e.target.value)}
        />
        <select
          className="form-select"
          value={selectedCategory}
          onChange={e => setSelectedCategory(e.target.value)}
        >
          <option value="">Pilih Kategori (opsional)</option>
          {categories.map((cat, index) => (
            <option key={index} value={cat}>{cat}</option>
          ))}
        </select>
        <button className="btn btn-primary" type="submit">Tambah</button>
      </form>

      {tasks.length === 0 ? (
        <p>Tidak ada tugas.</p>
      ) : (
        <ul className="list-group">
          {tasks.map(task => (
            <li key={task.id} className="list-group-item d-flex justify-content-between align-items-center">
              <div>
                <Link to={`/tasks/${task.id}`}>{task.title}</Link>
                {task.category && (
                  <small className="text-muted ms-2">[{task.category}]</small>
                )}
              </div>
              <button className="btn btn-danger btn-sm" onClick={() => handleDeleteTask(task.id)}>Hapus</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
