import React, { useState, useEffect } from 'react';

export default function CategoryList() {
  const [categories, setCategories] = useState(() => {
    const saved = localStorage.getItem('categories');
    return saved ? JSON.parse(saved) : [];
  });
  const [tasks, setTasks] = useState(() => {
    const saved = localStorage.getItem('tasks');
    return saved ? JSON.parse(saved) : [];
  });

  const [newCategory, setNewCategory] = useState('');
  const [selectedCategory, setSelectedCategory] = useState(null);

  // State untuk edit kategori
  const [editingCategory, setEditingCategory] = useState(null);
  const [editingValue, setEditingValue] = useState('');

  const handleAddCategory = (e) => {
    e.preventDefault();
    const trimmed = newCategory.trim();
    if (trimmed === '') return;

    if (categories.includes(trimmed)) {
      alert('Kategori sudah ada!');
      return;
    }

    const updatedCategories = [...categories, trimmed];
    setCategories(updatedCategories);
    localStorage.setItem('categories', JSON.stringify(updatedCategories));
    setNewCategory('');
  };

  const handleDeleteCategory = (category) => {
    const filteredCategories = categories.filter(cat => cat !== category);
    setCategories(filteredCategories);
    localStorage.setItem('categories', JSON.stringify(filteredCategories));

    const updatedTasks = tasks.map(task =>
      task.category === category ? { ...task, category: null } : task
    );
    setTasks(updatedTasks);
    localStorage.setItem('tasks', JSON.stringify(updatedTasks));

    if (selectedCategory === category) {
      setSelectedCategory(null);
    }
  };

  const handleStartEdit = (category) => {
    setEditingCategory(category);
    setEditingValue(category);
  };

  const handleSaveEdit = () => {
    const trimmed = editingValue.trim();
    if (trimmed === '') {
      alert('Kategori tidak boleh kosong!');
      return;
    }
    if (categories.includes(trimmed) && trimmed !== editingCategory) {
      alert('Kategori sudah ada!');
      return;
    }

    const updatedCategories = categories.map(cat =>
      cat === editingCategory ? trimmed : cat
    );
    setCategories(updatedCategories);
    localStorage.setItem('categories', JSON.stringify(updatedCategories));

    const updatedTasks = tasks.map(task =>
      task.category === editingCategory ? { ...task, category: trimmed } : task
    );
    setTasks(updatedTasks);
    localStorage.setItem('tasks', JSON.stringify(updatedTasks));

    setEditingCategory(null);
    setEditingValue('');
  };

  const handleCancelEdit = () => {
    setEditingCategory(null);
    setEditingValue('');
  };

  const filteredTasks = selectedCategory
    ? tasks.filter(task => task.category === selectedCategory)
    : [];

  return (
    <div className="container mt-4">
      <h2>Manajemen Kategori</h2>

      <form onSubmit={handleAddCategory} className="mb-3 d-flex gap-2">
        <input
          type="text"
          className="form-control"
          placeholder="Tambah kategori baru..."
          value={newCategory}
          onChange={e => setNewCategory(e.target.value)}
        />
        <button className="btn btn-primary" type="submit">Tambah</button>
      </form>

      <div className="row">
        {/* Tabel Kategori */}
        <div className="col-md-5">
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>Kategori</th>
                <th>Aksi</th>
              </tr>
            </thead>
            <tbody>
              {categories.length === 0 && (
                <tr>
                  <td colSpan="2" className="text-center">Tidak ada kategori.</td>
                </tr>
              )}
              {categories.map(cat => (
                <tr
                  key={cat}
                  className={selectedCategory === cat ? 'table-primary' : ''}
                  style={{ cursor: 'pointer' }}
                  onClick={() => setSelectedCategory(cat)}
                >
                  <td>
                    {editingCategory === cat ? (
                      <input
                        type="text"
                        className="form-control"
                        value={editingValue}
                        onChange={e => setEditingValue(e.target.value)}
                        onClick={e => e.stopPropagation()}
                      />
                    ) : (
                      cat
                    )}
                  </td>
                  <td>
                    {editingCategory === cat ? (
                      <>
                        <button
                          className="btn btn-success btn-sm me-2"
                          onClick={e => { e.stopPropagation(); handleSaveEdit(); }}
                        >
                          Simpan
                        </button>
                        <button
                          className="btn btn-secondary btn-sm"
                          onClick={e => { e.stopPropagation(); handleCancelEdit(); }}
                        >
                          Batal
                        </button>
                      </>
                    ) : (
                      <>
                        <button
                          className="btn btn-warning btn-sm me-2"
                          onClick={e => {
                            e.stopPropagation();
                            handleStartEdit(cat);
                          }}
                        >
                          Edit
                        </button>
                        <button
                          className="btn btn-danger btn-sm"
                          onClick={e => {
                            e.stopPropagation();
                            handleDeleteCategory(cat);
                          }}
                        >
                          Hapus
                        </button>
                      </>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Tugas Kategori */}
        <div className="col-md-7">
          {selectedCategory ? (
            <>
              <h4>Tugas dalam kategori: {selectedCategory}</h4>
              {filteredTasks.length === 0 ? (
                <p>Tidak ada tugas dalam kategori ini.</p>
              ) : (
                <ul className="list-group">
                  {filteredTasks.map(task => (
                    <li key={task.id} className="list-group-item">
                      {task.title}
                    </li>
                  ))}
                </ul>
              )}
            </>
          ) : (
            <p>Pilih kategori untuk melihat tugas.</p>
          )}
        </div>
      </div>
    </div>
  );
}
