const API_BASE_URL = 'http://localhost:6543'; // URL backend kamu

export async function login(username, password) {
  throw new Error('Fitur login belum terhubung ke backend');
}

export async function register(username, password) {
  throw new Error('Fitur register belum terhubung ke backend');
}

export async function fetchTasks() {
  const res = await fetch(`${API_BASE_URL}/tasks`);
  if (!res.ok) throw new Error('Gagal fetch tasks');
  return await res.json();
}

export async function fetchTaskById(id) {
  const response = await fetch(`${API_BASE_URL}/tasks/${id}`);
  if (!response.ok) throw new Error('Gagal mengambil detail tugas');
  return response.json();
}

export async function addTask(task) {
  const res = await fetch(`${API_BASE_URL}/tasks`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(task),
  });
  if (!res.ok) throw new Error('Gagal tambah task');
  return await res.json();
}

export async function updateTask(id, updatedFields) {
  const response = await fetch(`${API_BASE_URL}/tasks/${id}`, {
    method: 'PUT',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(updatedFields),
  });
  if (!response.ok) throw new Error('Gagal mengupdate tugas');
  return response.json();
}

export async function deleteTask(id) {
  const res = await fetch(`${API_BASE_URL}/tasks/${id}`, {
    method: 'DELETE',
  });
  if (!res.ok) throw new Error('Gagal hapus task');
  return true;
}

export async function fetchCategories() {
  const res = await fetch(`${API_BASE_URL}/categories`);
  if (!res.ok) throw new Error('Gagal fetch categories');
  return await res.json();
}

export async function addCategory(name) {
  const response = await fetch(`${API_BASE_URL}/categories`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({ name }),
  });
  if (!response.ok) throw new Error('Gagal menambah kategori');
  return response.json();
}

export async function deleteCategory(id) {
  const response = await fetch(`${API_BASE_URL}/categories/${id}`, {
    method: 'DELETE',
  });
  if (!response.ok) throw new Error('Gagal menghapus kategori');
  return true;
}
