import React, { useState, useEffect } from 'react';

export default function CategoryList() {
  const [categories, setCategories] = useState(() => {
    const saved = localStorage.getItem('categories');
    return saved ? JSON.parse(saved) : [];
  });
  const [newCategory, setNewCategory] = useState('');

  const handleAddCategory = (e) => {
    e.preventDefault();
    if (newCategory.trim() === '') return;

    const updatedCategories = [...categories, newCategory.trim()];
    setCategories(updatedCategories);
    localStorage.setItem('categories', JSON.stringify(updatedCategories));
    setNewCategory('');
  };

  const handleDeleteCategory = (category) => {
    const filtered = categories.filter(cat => cat !== category);
    setCategories(filtered);
    localStorage.setItem('categories', JSON.stringify(filtered));
  };

  return (
    <div className="container mt-4">
      <h2>Manajemen Kategori</h2>
      <form onSubmit={handleAddCategory} className="mb-3 d-flex gap-2">
        <input
          type="text"
          className="form-control"
          placeholder="Tambah kategori baru..."
          value={newCategory}
          onChange={e => setNewCategory(e.target.value)}
        />
        <button className="btn btn-primary" type="submit">Tambah</button>
      </form>

      {categories.length === 0 ? (
        <p>Tidak ada kategori.</p>
      ) : (
        <ul className="list-group">
          {categories.map(cat => (
            <li key={cat} className="list-group-item d-flex justify-content-between align-items-center">
              {cat}
              <button
                className="btn btn-danger btn-sm"
                onClick={() => handleDeleteCategory(cat)}
              >
                Hapus
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
